import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './CarouselComp.css'
import {Row,Col } from 'react-bootstrap';

// Yup validation schema


const optionsSchema = Yup.object().shape({
  selectedOptions: Yup.array()
    .min(1, 'Please select at least one option')
    .required('Please select at least one option'),
});

// Formik initial values
const initialValues = {
  selectedOptions: [],
};

const QuestionCarousel = () => (
  <Carousel 
    interval={null} 
    indicators={false} 
    controls={true}
    prevIcon={<i className="bi bi-arrow-left-circle-fill"></i>}
    nextIcon={<i className="bi bi-arrow-right-circle-fill"></i>}
   
  >

    <Carousel.Item>
      <Formik
        initialValues={initialValues}
        validationSchema={optionsSchema}
        onSubmit={(values) => {
          // Handle form submission
          alert(JSON.stringify(values, null, 2));
        }}
      >
        {({ errors, touched }) => (
          // <div>
          <Form className='component-car'>
            <Row>              
              <label>Which of the following best describes you
                <br></br>
                Check all that apply
              </label>
              {/* First column with three checkboxes */}
              <Col xs={12} md={6}>
                <div role="group" aria-labelledby="checkbox-group">
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Veteran" />
                    Veteran
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Family of veteran" />
                    Family of veteran
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="International student" />
                    International student
                  </label>
                </div>
              </Col>
              {/* Second column with two checkboxes */}
              <Col xs={12} md={6}>
                <div role="group" aria-labelledby="checkbox-group">
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="With disability" />
                    With disability
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Other" />
                    Other
                  </label>
                </div>
              </Col>
            </Row>
              {/* Error message */}
              {errors.selectedOptions && touched.selectedOptions ? (
                <div>{errors.selectedOptions}</div>
              ) : null}
              <br></br>
              <button type="submit">Submit</button>
            


            
          </Form>



          // </div>
        )}
      </Formik>

    </Carousel.Item>

    <Carousel.Item>
      <div>This is two <br></br> This is four</div>
    </Carousel.Item>
    <Carousel.Item>
      <div>This is three</div>
    </Carousel.Item>
  </Carousel>
);

export default QuestionCarousel;






























































