// src/components/QuestionCarousel.js

import React , {useState} from 'react';
import Carousel from 'react-bootstrap/Carousel'
// import { Carousel, Form } from 'react-bootstrap';
import  Form   from 'react-bootstrap/Form';

// const QuestionCarousel = () => {
  
function QuestionCarousel() {
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };
  return (
    <Carousel activeIndex={index} onSelect={handleSelect} interval={null}>
      <Carousel.Item>
        <div className="carousel-modal">
          <h3>Question 1</h3>
          <Form>
            <Form.Group>
              <Form.Label>Which of the following best describes you? Check all that apply.</Form.Label>
              <Form.Check type="checkbox" label="Veteran" />
              <Form.Check type="checkbox" label="Family of Veteran" />
              <Form.Check type="checkbox" label="International Student" />
              <Form.Check type="checkbox" label="With Disability" />
            </Form.Group>
            <Form.Group>
              <Form.Label>Which race do you identify with?</Form.Label>
              <Form.Control as="select">
                <option>African American/Black</option>
                <option>White</option>
                <option>Other</option>
              </Form.Control>
            </Form.Group>
          </Form>
        </div>
      </Carousel.Item>
      <Carousel.Item>
        <div className="carousel-modal">
          <h3>This is two</h3>
          <p> this is the second slide </p>
         
        </div>
      </Carousel.Item>
      <Carousel.Item>
        <div className="carousel-modal">
          <h3>This is three</h3>
         
        </div>
      </Carousel.Item>
    </Carousel>
  );
};

export default QuestionCarousel;
