import React,{useState} from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './CarouselComp.css'
import Results from './results';
import {Row,Col } from 'react-bootstrap';

// Yup validation schema
const optionsSchema = Yup.object().shape({
  selectedOptions: Yup.array()
    .min(1, 'Please select at least one option')
    .required('Please select at least one option'),
    race: Yup.string()
    .required('Please select your race')
    .notOneOf([''], 'Please select your race'),
    gender: Yup.string()
    .required('Please select your Gender')
    .notOneOf([''], 'Please select your Gender'),
    minPrice: Yup.number()
    .min(0, 'Minimum price must be greater than or equal to 0')
    .required('Minimum price is required'),
    maxPrice: Yup.number()
    .moreThan(Yup.ref('minPrice'), 'Maximum price must be greater than minimum price')
    .required('Maximum price is required'),
    income:Yup.number()
    .required ('Please select an income bracket')
    .notOneOf(['', 'Please select an income bracket']),
    address: Yup.string().required('Address is required'),
    city: Yup.string().required('City is required'),
    zip: Yup.string().matches(/^[0-9]{5}$/, 'Invalid zip code').required('Zip code is required'),
    state: Yup.string().required('State is required'),
  });



// Formik initial values
const initialValues = {
  selectedOptions: [],
  race:'',
  gender:'',
  minPrice:'',
  maxPrice:'',
  income:'',
  address: '',
  city: '',
  zip: '',
  state: '',
};



 


const QuestionCarousel = () => {
  const [submissionSuccessful, setSubmissionSuccessful] = useState(false);

  async function sendData(data) {
    try{
    const response = await fetch('http://localhost:5000/submit', {
      method: 'POST', // Specifies that this is a POST request
      headers: {
        'Content-Type': 'application/json', // Indicates the content type being sent
      },
      body: JSON.stringify(data), // Converts the data object to a JSON string
    });
  
    if (response.ok) {
      setSubmissionSuccessful(true);
        // const recommendations = await response.json();
        // Now you can set these recommendations to state and display them as needed
    } else {
        // Handle errors
        console.error('Failed to fetch recommendations:', response);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
  }
  };

  if (submissionSuccessful) {
    console.log('submission successful')
    // Render Results component if submission was successful
    return <Results /> ;
  }

  return (
  <Carousel 
    interval={null} 
    indicators={false} 
    controls={true}
    // prevIcon={<i className="bi bi-arrow-left-circle-fill"></i>}
    // nextIcon={<i className="bi bi-arrow-right-circle-fill"></i>}   
  >

    <Carousel.Item>
      <Formik
        initialValues={initialValues}
        validationSchema={optionsSchema}
        onSubmit={async (values, actions) => {
          try {
            const response = await sendData(values);
            console.log(response); // Process the response as needed
            actions.setSubmitting(false); // Set submitting to false after submission
          } catch (error) {
            console.error("Error sending data: ", error);
            actions.setSubmitting(false); // Set submitting to false if there's an error
          }
        }}
        >      

    
        {({ errors, touched,handleChange }) => (
          // <div>
          <Form className='component-car'>
            <Row>              
              <label>Which of the following best describes you
                <br></br>
                Check all that apply
              </label>
              {/* First column with three checkboxes */}
              <Col xs={12} md={6}>
                <div role="group" aria-labelledby="checkbox-group">
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Veteran" />
                    Veteran
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Family of veteran" />
                    Family of veteran
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="International student" />
                    International student
                  </label>
                </div>
              </Col>
              {/* Second column with two checkboxes */}
              <Col xs={12} md={6}>
                <div role="group" aria-labelledby="checkbox-group">
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="With disability" />
                    With disability
                  </label>
                  <br></br>
                  <label>
                    <Field type="checkbox" name="selectedOptions" value="Other" />
                    Other
                  </label>
                </div>

                {/* Error message */}
                {errors.selectedOptions && touched.selectedOptions ? (
                  <div>{errors.selectedOptions}</div>
                ) : null}
            </Col>
          </Row>

          <Row>
            <Col>
              <label htmlFor="race">Which race do you identify with.</label>
              <Field as="select" name="race" className="form-select">
                <option value="" label="Select a race" />
                <option value="White" label="White" />
                <option value="Black" label="Black" />
                <option value="NativeAmerican/AlaskaNative" label="Native American/Alaska Native" />
                <option value="Asian" label="Asian" />
                <option value="NativeHawaiian/OtherPacific" label="Native Hawaiian/Other Pacific" />
                <option value="Other" label="Other" />
              </Field>
              {errors.race && touched.race ? (
                <div>{errors.race}</div>
              ) : null}
            </Col>
          </Row> 

          <Row>
            <Col>
              <label htmlFor="gender">Which Gender do you identify with.</label>
              <Field as="select" name="gender" className="form-select">
                <option value="" label="Select a Gender" />
                <option value="Woman" label="Woman" />
                <option value="Man" label="Man" />
                <option value="Transgender Woman" label="Transgender Woman" />
                <option value="Transgender Man" label="Transgender Man"/>
                <option value="Non-Binary" label="Non-Binary" />
                <option value="Prefer not to state" label="Prefer not to state" />
                <option value="Not listed" label="Not listed" />
              </Field>
              {errors.race && touched.gender ? (
                <div>{errors.gender}</div>
              ) : null}
            </Col>
          </Row>

          <Row>
            <Col>
            <div style={{paddingBottom : "10px"}}> 
              <label htmlFor='price'> What is your price range </label>
            </div>

            <div style={{paddingBottom : "10px"}}>
              <label htmlFor="minPrice">Minimum Price</label>
              <Field name="minPrice" type="number" />
              {errors.minPrice && touched.minPrice ? <div>{errors.minPrice}</div> : null}
            </div>

            <div >
              <label htmlFor="maxPrice">Maximum Price</label>
              <Field name="maxPrice" type="number" />
              {errors.maxPrice && touched.maxPrice ? <div>{errors.maxPrice}</div> : null}
            </div>           
            </Col>
          </Row> 

          <Row>
            <Col>
              <label htmlFor="race">What is your/your family's income bracket .</label>
              <Field as="select" name="income" className="form-select">
                <option value="" label="Select an income bracket" />
                <option value="0" label="0-30,000" />
                <option value="30001 " label="30,001 - 48,000" />
                <option value="48001" label="48,001- 75,000" />
                <option value="75001" label=" above 75,000"/>
                {/* <option value="NativeHawaiian/OtherPacific" label="Native Hawaiian/Other Pacific" />
                <option value="Other" label="Other" /> */}
              </Field>
              {errors.income && touched.income ? (
                <div>{errors.income}</div>
              ) : null}
            </Col>
          </Row> 

          <Row>          
          <div>
            <label htmlFor="address">Address</label>
            <Field name="address" type="text" />
            {errors.address && touched.address ? <div>{errors.address}</div> : null}
          </div>

          <div>
            <label htmlFor="city">City</label>
            <Field name="city" type="text" />
            {errors.city && touched.city ? <div>{errors.city}</div> : null}
          </div>

          <div>
            <label htmlFor="zip">Zip Code</label>
            <Field name="zip" type="text" />
            {errors.zip && touched.zip ? <div>{errors.zip}</div> : null}
          </div>

          <div>
            <label htmlFor="state">State</label>
            <Field as="select" name="state" onChange={handleChange}>          
              <option value="" label="Select State" />
              <option value="AZ" label="AZ" />
              <option value="OH" label="OH"/>             
            </Field>
            {errors.state && touched.state ? <div>{errors.state}</div> : null}
          </div>
          </Row>

          <br></br>
          <button type="submit">Submit</button>                
          </Form>
          // </div>
        )}
      </Formik>

    </Carousel.Item>

    {/* <Carousel.Item>
      <div>This is two <br></br> This is four</div>
    </Carousel.Item>
    <Carousel.Item>
      <div>This is three</div>
    </Carousel.Item> */}
  </Carousel>
  );
};



export default QuestionCarousel;






























































